import copyIconRegular from "../../tile-icon/copy-regular-full.svg";

const view = (state, {updateState,dispatch}) => {
	let {textToCopy} = state.properties;
	let {copied} = state;
	const handleCopy = () => {
		if (textToCopy) {
			navigator.clipboard.writeText(textToCopy).then(() => {
				updateState({copied: true});
				dispatch("TEXT_COPIED",
					{
						content : textToCopy
					}
				);
				setTimeout(() => updateState({copied: false}), 500);
			});
		}
	};
	return (
		<img src={copied?copyIconSolid:copyIconRegular} on-click={handleCopy} width={state.properties.width} />
	);
};

